import React,{useState, useEffect, useCallback} from 'react'
import axios from 'axios'
import Review from './Review'
import CafeHeader from './CafeHeader'

const CafeReviews = ({ match }) => {
  const [cafe, setCafe] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [rating,setRating] = useState(null)
  const getRating = useCallback(data =>setRating())

  useEffect(() => {
    axios.get(`/api/cafe/${match.params.id}`).then((result) => {
      setCafe(result.data);
    })
    axios.get("http://localhost:5000/api/all-reviews")
      .then((review) => {
        setReviews(review.data);
      })
      .catch((err) => {
        console.log(err);
      })
  }, [])

  let filteredReviews = reviews.filter((review) => {
    return review.cafeName === cafe.cafeName;
  })

  return (
    <div>
      {rating}
      <CafeHeader cafe={cafe}/>
      <div className="reviews-container">
      <Review reviews={filteredReviews} setRating = {setRating}/>
      </div>
    </div>
  )
}

export default CafeReviews
